use super::Field;
use proc_macro2::{Ident, TokenStream};
use syn::Generics;

pub(super) struct FormDataBuilder<'a> {
	pub(super) name: &'a Ident,
	pub(super) err_ident: &'a Ident,
	pub(super) ident: Ident,
	pub(super) generics: &'a Generics,
	pub(super) fields: &'a [Field]
}

impl<'a> FormDataBuilder<'a> {
	pub(super) fn gen_struct(&self) -> TokenStream {
		let ident = &self.ident;
		let (impl_gen, _, were) = self.generics.split_for_impl();

		let field_names = self.fields.iter().map(|f| &f.ident);
		let field_types = self.fields.iter().map(|f| &f.ty);

		quote! {
			#[doc(hidden)]
			struct #ident #impl_gen #were {
				#( #field_names: Option<#field_types> ),*
			}
		}
	}

	pub(super) fn gen_default_impl(&self) -> TokenStream {
		let ident = &self.ident;
		let (impl_gen, ty_gen, were) = self.generics.split_for_impl();

		let field_names = self.fields.iter().map(|f| &f.ident);

		quote! {
			impl #impl_gen Default for #ident #ty_gen #were {
				fn default() -> Self {
					Self {
						#( #field_names: None ),*
					}
				}
			}
		}
	}

	pub(super) fn gen_builder_impl(&self) -> TokenStream {
		let name = &self.name;
		let err_ident = &self.err_ident;
		let ident = &self.ident;
		let (impl_gen, ty_gen, were) = self.generics.split_for_impl();

		let field_names = self.fields.iter().map(|f| &f.ident).collect::<Vec<_>>();
		let field_types = self.fields.iter().map(|f| &f.ty);

		quote! {
			impl #impl_gen ::gotham_formdata::internal::FormDataBuilder for #ident #ty_gen #were {
				type Data = #name #ty_gen;
				type Err = #err_ident;

				fn add_entry<'a>(
						&'a mut self,
						name: ::std::borrow::Cow<'a, str>,
						value: ::gotham_formdata::conversion::ByteStream<::gotham_formdata::Error<Self::Err>>
				) -> ::gotham_formdata::internal::FormDataBuilderFuture<'a, Self::Err> {
					#[allow(unused_imports)]
					use ::gotham_formdata::{conversion::prelude::*, export::{FutureExt, StreamExt}};

					async move {
						let name: &str = &name;
						match name {
							#(stringify!(#field_names) => {
								log::debug!("Found value for field {}", name);
								let value_parsed = <#field_types>::convert_byte_stream(name, value).await?;
								self.#field_names.replace(value_parsed);
								Ok(())
							},)*
							_ => {
								log::debug!("Found an unknown field: {}", name);
								Err(::gotham_formdata::Error::UnknownField(name.to_string()))
							}
						}
					}.boxed()
				}

				fn build(self) -> Result<#name #ty_gen, ::gotham_formdata::Error<Self::Err>> {
					Ok(#name #ty_gen {
						#( #field_names: self.#field_names.ok_or(::gotham_formdata::Error::MissingField(stringify!(#field_names).to_owned()))? ),*
					})
				}
			}
		}
	}
}

use crate::util::*;
use proc_macro2::{Ident, Span, TokenStream};
use syn::{
	parse::{Parse, ParseStream},
	punctuated::Punctuated,
	spanned::Spanned,
	Error, Expr, Result, Token, Type
};

#[allow(dead_code)]
struct FieldMeta {
	ident: Ident,
	eq_token: Token![=],
	expr: Expr
}

impl Parse for FieldMeta {
	fn parse(input: ParseStream<'_>) -> Result<Self> {
		Ok(Self {
			ident: input.parse()?,
			eq_token: input.parse()?,
			expr: input.parse()?
		})
	}
}

pub(super) struct Field {
	pub(super) ident: Ident,
	pub(super) ty: Type,
	pub(super) validator: Option<TokenStream>,
	pub(super) validation_error: Option<Expr>
}

impl Field {
	pub(super) fn new(field: syn::Field) -> Result<Self> {
		let span = field.span();

		let mut ident = field
			.ident
			.ok_or(Error::new(span, "Fields without an ident are not supported"))?;
		ident.set_span(Span::call_site());

		let ty = field.ty;
		// unfortunately, we cannot change spans of types

		let mut validator: Option<TokenStream> = None;
		let mut validation_error: Option<Expr> = None;
		for attr in field.attrs {
			if !attr.path.ends_with("validate") {
				continue;
			}
			let list = attr.parse_args_with(Punctuated::<FieldMeta, Token![,]>::parse_separated_nonempty)?;
			// parse_separated_nonempty guarantees that there is at least one element in the list
			for meta in list.into_iter() {
				let name = meta.ident;
				let expr = meta.expr;

				let new_validator = match name.to_string().as_ref() {
					// custom error message
					"error" => {
						if validation_error.is_some() {
							return Err(Error::new(name.span(), "'error' must not appear more than once"));
						}
						validation_error = Some(expr);
						continue;
					},

					// custom validator
					"validator" => {
						// TODO this code makes sure to emit an error message pointing to the macro's
						// input, but there should be a better way to do this
						quote_spanned! { expr.span() =>
							{
								let validator = #expr;
								::gotham_formdata::internal::assert_validator::<_, _>(&validator);
								validator
							}
						}
					},

					// min_length validator
					"min_length" => quote!(::gotham_formdata::validate::MinLengthValidator::new(#expr)),

					// max_length validator
					"max_length" => quote!(::gotham_formdata::validate::MaxLengthValidator::new(#expr)),

					// min validator
					"min" => quote!(::gotham_formdata::validate::MinValidator::<#ty>::new(#expr)),

					// max validator
					"max" => quote!(::gotham_formdata::validate::MaxValidator::<#ty>::new(#expr)),

					// regex validator
					"regex" => {
						if cfg!(not(feature = "regex")) {
							return Err(Error::new(
								name.span(),
								"You need to enable the 'regex' feature of gotham_formdata to enable the regex validator"
							));
						}

						let regex_ident = format_ident!("{}_validation_regex", ident.to_string());
						quote!({
							static #regex_ident: ::gotham_formdata::validate::LazyRegex = ::gotham_formdata::validate::LazyRegex::new(#expr);
							::gotham_formdata::validate::RegexValidator::new(#regex_ident.get().expect("Invalid Regex"))
						})
					},

					// expected validator
					"expected" => quote!(::gotham_formdata::validate::ExpectedValidator::new(#expr)),

					_ => return Err(Error::new(name.span(), "Unknown key for attribute validate"))
				};

				validator = match validator {
					Some(old_validator) => Some(quote! {
						{
							let first_validator = #old_validator;
							let second_validator = #new_validator;
							::gotham_formdata::validate::CombinedValidator::new(first_validator, second_validator)
						}
					}),
					None => Some(new_validator)
				};
			}
		}

		Ok(Self {
			ident,
			ty,
			validator,
			validation_error
		})
	}
}

use crate::util::*;
use proc_macro2::{Span, TokenStream};
use syn::{Data, DeriveInput, Error, Fields, Result};

mod builder;
use builder::FormDataBuilder;

mod field;
use field::Field;

mod validation_error;
use validation_error::ValidationError;

pub(super) fn expand(input: DeriveInput) -> Result<TokenStream> {
	let name = &input.ident;
	let (impl_gen, ty_gen, were) = input.generics.split_for_impl();
	let strukt = match input.data {
		Data::Struct(strukt) => strukt,
		_ => {
			return Err(Error::new(
				Span::call_site(),
				"#[derive(FormData)] can only be used on structs"
			))
		},
	};

	let fields = match strukt.fields {
		Fields::Named(named) => named.named.into_iter().map(|field| Field::new(field)).collect_to_result()?,
		Fields::Unnamed(_) => {
			return Err(Error::new(
				Span::call_site(),
				"#[derive(FormData)] cannot be used on tuple structs"
			))
		},
		Fields::Unit => Vec::new()
	};

	let validation_error = ValidationError {
		name,
		vis: &input.vis,
		ident: format_ident!("{}ValidationError", name),
		fields: &fields
	};
	let err_ident = &validation_error.ident;

	let builder = FormDataBuilder {
		name,
		err_ident,
		ident: format_ident!("{}FormDataBuilder", name),
		generics: &input.generics,
		fields: &fields
	};
	let builder_ident = &builder.ident;

	let validation_error_struct = validation_error.gen_struct();

	let builder_struct = builder.gen_struct();
	let builder_default_impl = builder.gen_default_impl();
	let builder_builder_impl = builder.gen_builder_impl();

	let field_idents = fields.iter().map(|f| &f.ident);
	let field_validators = fields.iter().map(|f| {
		f.validator
			.as_ref()
			.map(|v| quote!(Some({ #v })))
			.unwrap_or_else(|| quote!(::std::option::Option::<()>::None))
	});
	let field_validation_errors = fields.iter().map(|f| {
		f.validation_error
			.as_ref()
			.map(|err| quote!(Some(#err)))
			.unwrap_or_else(|| quote!(::std::option::Option::<String>::None))
	});

	let validate_trait = format_ident!("Validate{}FormData", name);

	let mut dummy = format_ident!("_IMPL_FORMDATA_FOR_{}", name);
	dummy.set_span(Span::call_site());
	Ok(quote! {
		#validation_error_struct

		#[allow(non_upper_case_globals)]
		static #dummy: () = {
			#builder_struct
			#builder_default_impl
			#builder_builder_impl

			#[doc(hidden)]
			trait #validate_trait {
				fn validate(&self) -> Result<(), #err_ident>;
			}

			impl #impl_gen #validate_trait for #name #ty_gen #were {
				#[doc(hidden)]
				fn validate(&self) -> Result<(), #err_ident> {
					::log::debug!("Validating Form Data for type {}", stringify!(#name));

					#({
						const name: &str = stringify!(#field_idents);
						let value = &self.#field_idents;
						let validator = #field_validators;
						let validation_error = #field_validation_errors;
						if let Some(validator) = validator {
							::gotham_formdata::validate::Validator::validate(validator, value)
								.map_err(|err| {
									match validation_error {
										Some(ve) => #err_ident::invalid(name, ve),
										None     => #err_ident::invalid(name, err)
									}
								})?;
						}
					})*

					Ok(())
				}
			}

			impl #impl_gen ::gotham_formdata::FormData for #name #ty_gen #were {
				type Err = ::gotham_formdata::Error<#err_ident>;

				fn parse_form_data(state: &mut ::gotham_formdata::export::State) -> ::gotham_formdata::FormDataFuture<Self> {
					use ::gotham_formdata::export::FutureExt;

					let content_type = ::gotham_formdata::internal::get_content_type(state);
					let body = ::gotham_formdata::internal::get_body(state);

					async move {
						let content_type = content_type?;
						::log::debug!("Parsing Form Data for type {} with Content-Type {}", stringify!(#name), content_type);

						let res = ::gotham_formdata::internal::parse::<#builder_ident #ty_gen>(body, content_type).await?;
						#validate_trait::validate(&res).map_err(|err| ::gotham_formdata::Error::InvalidData(err))?;
						Ok(res)
					}.boxed()
				}
			}
		};
	})
}

use super::Field;
use heck::{CamelCase, SnakeCase};
use proc_macro2::{Ident, Span, TokenStream};
use syn::{LitStr, Visibility};

pub(super) struct ValidationError<'a> {
	pub(super) name: &'a Ident,
	pub(super) vis: &'a Visibility,
	pub(super) ident: Ident,
	pub(super) fields: &'a [Field]
}

impl<'a> ValidationError<'a> {
	pub(super) fn gen_struct(&self) -> TokenStream {
		let name = &self.name;
		let vis = &self.vis;
		let ident = &self.ident;

		let doc = format!(
			"This error is returned when form data parsed for [{}] failed validation.",
			name
		);

		let field_names = self
			.fields
			.iter()
			.map(|f| LitStr::new(&f.ident.to_string(), Span::call_site()))
			.collect::<Vec<_>>();
		let variant_idents = self
			.fields
			.iter()
			.map(|f| format_ident!("{}Invalid", f.ident.to_string().to_camel_case()))
			.collect::<Vec<_>>();
		let invalid_idents = self
			.fields
			.iter()
			.map(|f| format_ident!("invalid_{}", f.ident.to_string().to_snake_case()))
			.collect::<Vec<_>>();
		// TODO variant_error_types

		quote! {
			#[doc = #doc]
			#[derive(Debug)]
			#vis enum #ident {
				#(
					#variant_idents(String)
				),*
			}

			impl #ident {
				#[doc(hidden)]
				fn invalid<Err: ::std::fmt::Display>(field: &str, err: Err) -> Self {
					match field {
						#(
							#field_names => Self::#invalid_idents(err),
						)*
						_ => panic!("Unknown field {}", field)
					}
				}

				#(
					fn #invalid_idents<Err: ::std::fmt::Display>(err: Err) -> Self {
						Self::#variant_idents(err.to_string())
					}
				)*

				#vis fn field_name(&self) -> &'static str {
					match self {
						#( Self::#variant_idents(_) => #field_names, )*
						_ => unreachable!()
					}
				}
			}

			impl ::std::fmt::Display for #ident {
				fn fmt(&self, f: &mut ::std::fmt::Formatter<'_>) -> ::std::fmt::Result {
					write!(f, "Invalid value for field {}:", self.field_name())?;
					match self {
						#( Self::#variant_idents(err) => write!(f, "{}", err), )*
						_ => unreachable!()
					}
				}
			}

			impl ::std::error::Error for #ident {}
		}
	}
}

//! This crate implements derive macros for the `gotham_formdata` crate.
#![warn(missing_docs, rust_2018_idioms)]
#![deny(unreachable_pub)]

#[macro_use]
extern crate quote;

use proc_macro::TokenStream;
use proc_macro2::TokenStream as TokenStream2;
use syn::{parse_macro_input, DeriveInput, Result};

mod form_data;
mod util;

#[inline]
fn print_tokens(tokens: TokenStream2) -> TokenStream {
	//eprintln!("{}", tokens);
	tokens.into()
}

#[inline]
fn expand_derive<F>(input: TokenStream, expand: F) -> TokenStream
where
	F: FnOnce(DeriveInput) -> Result<TokenStream2>
{
	print_tokens(expand(parse_macro_input!(input)).unwrap_or_else(|err| err.to_compile_error()))
}

/// This derive macro implements `FormData` for the struct it is invoked on. Enums, unions and
/// tuple structs are not supported.
#[proc_macro_derive(FormData, attributes(validate))]
pub fn derive_form_data(input: TokenStream) -> TokenStream {
	expand_derive(input, form_data::expand)
}

use proc_macro2::Span;
use syn::{Error, Path};

pub(crate) trait CollectToResult {
	type Item;

	fn collect_to_result(self) -> Result<Vec<Self::Item>, Error>;
}

impl<Item, I> CollectToResult for I
where
	I: Iterator<Item = Result<Item, Error>>
{
	type Item = Item;

	fn collect_to_result(self) -> Result<Vec<Item>, Error> {
		self.fold(<Result<Vec<Item>, Error>>::Ok(Vec::new()), |res, code| match (code, res) {
			(Ok(code), Ok(mut codes)) => {
				codes.push(code);
				Ok(codes)
			},
			(Ok(_), Err(errors)) => Err(errors),
			(Err(err), Ok(_)) => Err(err),
			(Err(err), Err(mut errors)) => {
				errors.combine(err);
				Err(errors)
			}
		})
	}
}

pub(crate) trait PathEndsWith {
	fn ends_with(&self, s: &str) -> bool;
}

impl PathEndsWith for Path {
	fn ends_with(&self, s: &str) -> bool {
		self.segments.last().map(|segment| segment.ident.to_string()).as_deref() == Some(s)
	}
}

pub(crate) trait WithSpan {
	fn with_span(self, span: Span) -> Self;
}

impl WithSpan for Error {
	fn with_span(self, span: Span) -> Self {
		let mut err: Option<Self> = None;
		for old_err in self {
			let new_err = Error::new(span, old_err);
			err = match err {
				Some(mut err) => {
					err.combine(new_err);
					Some(err)
				},
				None => Some(new_err)
			};
		}
		err.unwrap()
	}
}

/*!
This mod contains conversion traits for common used types, that allows them to be created from
a stream of bytes. Furthermore, it allows every type that implements [FromStr] plus some other
common types to be converted.

**DO NOT IMPLEMENT ANY OF THESE TRAITS MANUALLY!** If you do, it will likely result in compile
errors when the compiler cannot choose which trait to use in the proc-macro generated code.
Instead, if you want to provide a custom conversion method, just implement it as a method for
your type:

```rust
use futures_util::{FutureExt, StreamExt};
use gotham_formdata::{FormData, conversion::{ByteStream, ConversionFuture}};

/// This type parses Base64-encoded values to a [Vec<u8>].
struct Base64(Vec<u8>);

impl Base64 {
	// the method signature needs to be roughly equivalent to this
	fn convert_byte_stream<'a, E>(
			name: &'a str,
			mut stream: ByteStream<gotham_formdata::Error<E>>
	) -> ConversionFuture<'a, Self, gotham_formdata::Error<E>>
	where
		E: std::error::Error + 'a
	{
		async move {
			let mut buf: Vec<u8> = Vec::new();
			while let Some(data) = stream.next().await {
				buf.extend_from_slice(data?.as_ref());
			}

			let data = base64::decode(&buf)
					.map_err(|err| gotham_formdata::Error::IllegalField(name.to_owned(), err.into()))?;
			Ok(Self(data))
		}.boxed()
	}
}
# mod base64 { pub fn decode(input: &[u8]) -> Result<Vec<u8>, std::convert::Infallible> { unimplemented!() } }
```
*/

use crate::Error;
use futures_util::{
	future::FutureExt,
	stream::{Stream, StreamExt}
};
use gotham::{anyhow, hyper::body::Bytes};
use std::{future::Future, pin::Pin, str::FromStr};

/// Re-exports for use in derive macro.
#[doc(hidden)]
pub mod prelude {
	pub use super::{ConvertFromStr, ConvertRawBytes};
}

/// A stream of bytes.
pub type ByteStream<Err> = Pin<Box<dyn Stream<Item = Result<Bytes, Err>> + Send>>;
/// The future returned from conversion methods.
pub type ConversionFuture<'a, T, Err> = Pin<Box<dyn Future<Output = Result<T, Err>> + Send + 'a>>;

/// This trait is used to convert types that implement [FromStr] from a stream of bytes.
///
/// **DO NOT IMPLEMENT MANUALLY!** Look at the [module documentation](self) for an example how
/// to convert custom types.
pub trait ConvertFromStr<Err>: Sized {
	/// Perform the conversion.
	fn convert_byte_stream<'a>(name: &'a str, stream: ByteStream<Err>) -> ConversionFuture<'a, Self, Err>;
}

impl<E, T> ConvertFromStr<Error<E>> for T
where
	E: std::error::Error,
	T: FromStr,
	T::Err: Into<anyhow::Error>
{
	fn convert_byte_stream<'a>(name: &'a str, mut stream: ByteStream<Error<E>>) -> ConversionFuture<'a, Self, Error<E>> {
		async move {
			let mut buf = String::new();
			while let Some(data) = stream.next().await {
				let data = data?;
				let str = String::from_utf8_lossy(data.as_ref());
				buf.push_str(&str);
			}

			buf.parse::<Self>()
				.map_err(|err| Error::IllegalField(name.to_owned(), err.into()))
		}
		.boxed()
	}
}

/// This trait is used to convert `Vec<u8>` and similar types from a stream of bytes.
///
/// **DO NOT IMPLEMENT MANUALLY!** Look at the [module documentation](self) for an example how
/// to convert custom types.
pub trait ConvertRawBytes<'a, Err>: Sized {
	/// Perform the conversion.
	fn convert_byte_stream(name: &'a str, stream: ByteStream<Err>) -> ConversionFuture<'a, Self, Err>;
}

impl<'a, E: 'a> ConvertRawBytes<'a, E> for Vec<u8> {
	fn convert_byte_stream(_name: &'a str, mut stream: ByteStream<E>) -> ConversionFuture<'a, Self, E> {
		async move {
			let mut buf: Vec<u8> = Vec::new();
			while let Some(data) = stream.next().await {
				buf.extend_from_slice(&data?);
			}
			Ok(buf)
		}
		.boxed()
	}
}

use gotham::anyhow;
use mime::Mime;
use thiserror::Error;

#[derive(Debug, Error)]
#[non_exhaustive]
/// This error type is used when parsing form data from a request body was unsuccessful.
pub enum Error<Err: std::error::Error + 'static> {
	/// The body of the request could not be read.
	#[error("The body of the request could not be read")]
	IllegalBody(#[from] gotham::hyper::Error),
	/// The content type of the body was not a valid mime type.
	#[error("The 'Content-Type' header could not be parsed: {0}")]
	IllegalContentType(#[from] mime::FromStrError),
	/// The content type of the body contained unreadable bytes.
	#[error("The 'Content-Type' header could not be parsed: {0}")]
	IllegalContentTypeValue(#[from] gotham::hyper::header::ToStrError),
	/// The value of a field could not be parsed into that field's type.
	#[error("The field {0} could not be parsed: {1}")]
	IllegalField(String, #[source] anyhow::Error),
	/// The body was parsed but contained data that did not pass validation.
	#[error("The body contained invalid data: {0}")]
	InvalidData(#[source] Err),
	/// An I/O error occured while reading the body.
	#[error("I/O Error while reading body: {0}")]
	IoError(#[from] std::io::Error),
	/// The body was a multipart body but did not specify a boundary.
	#[error("The request failed to specify the multipart boundary")]
	MissingBoundary,
	/// The request did not specify a content type.
	#[error("The request is missing a 'Content-Type' header")]
	MissingContentType,
	/// One of the multipart parts is missing a `Content-Disposition` header.
	#[error("Missing a 'Content-Disposition' header")]
	MissingContentDisposition,
	/// The body is missing a required field.
	#[error("Missing Field '{0}'")]
	MissingField(String),
	/// The multipart format was invalid.
	#[error("Multipart error: {0}")]
	MultipartError(#[from] multer::Error),
	/// The body's content type is not supported.
	#[error("Unknown 'Content-Type' header value: {0}")]
	UnknownContentType(Mime),
	/// The body contained a field that was not expected.
	#[error("Unknown Field '{0}'")]
	UnknownField(String)
}

use gotham::state::State;
use std::{future::Future, pin::Pin};

/// This is the return type of [FormData::parse_form_data].
#[allow(type_alias_bounds)]
pub type FormDataFuture<T: FormData> = Pin<Box<dyn Future<Output = Result<T, T::Err>> + Send>>;

/**
This is the trait implemented by `#[derive(FormData)]`. It provides a method to parse the struct
it is implemented for to be parsed from the request body contained in gotham's state.

You usually don't implement this trait directly, use the derive macro instead.
*/
pub trait FormData: Sized {
	/// The error type returned when parsing the request body was unsuccessful.
	type Err;

	/// Parse the struct from the request body contained in gotham's state.
	fn parse_form_data(state: &mut State) -> FormDataFuture<Self>;
}

/**
This is the equivalent of [FormData] from the state's perspective. Use this if you prefer
`state.parse_form_data::<MyData>()?` over `MyData::parse_form_data(&mut state)?`.
*/
pub trait FormDataFromState {
	/// Parse `T` from the request body contained in this state.
	fn parse_form_data<T: FormData>(&mut self) -> FormDataFuture<T>;
}

impl FormDataFromState for State {
	fn parse_form_data<T: FormData>(&mut self) -> FormDataFuture<T> {
		T::parse_form_data(self)
	}
}

