		body::{self, Body, Bytes},
		header::{HeaderMap, CONTENT_TYPE}
	},
	state::State
};
use mime::{Mime, APPLICATION_WWW_FORM_URLENCODED, BOUNDARY, MULTIPART_FORM_DATA};
use multer::Multipart;
use std::{borrow::Cow, future::Future, pin::Pin};

pub fn assert_validator<V: Validator<T>, T: ?Sized>(_: &V) {}

pub type FormDataBuilderFuture<'a, Err> = Pin<Box<dyn Future<Output = Result<(), Error<Err>>> + Send + 'a>>;

pub trait FormDataBuilder: Default {
	type Data: FormData;
	/// The error that can occur during verification.
	type Err: std::error::Error + 'static;

	fn add_entry<'a>(
		&'a mut self,
		name: Cow<'a, str>,
		value: ByteStream<Error<Self::Err>>
	) -> FormDataBuilderFuture<'a, Self::Err>;
	fn build(self) -> Result<Self::Data, Error<Self::Err>>;
}

pub fn get_content_type<Err: std::error::Error>(state: &State) -> Result<Mime, Error<Err>> {
	let headers: &HeaderMap = state.borrow();
	Ok(headers
		.get(CONTENT_TYPE)
		.ok_or(Error::MissingContentType)?
		.to_str()?
		.parse()?)
}

pub fn get_body(state: &mut State) -> Body {
	state.take()
}

pub async fn parse<T: FormDataBuilder>(body: Body, content_type: Mime) -> Result<T::Data, Error<T::Err>> {
	if is_urlencoded(&content_type) {
		parse_urlencoded::<T>(body).await
	} else if is_multipart(&content_type) {
		parse_multipart::<T>(body, &content_type).await
	} else {
		Err(Error::UnknownContentType(content_type))
	}
}

fn is_urlencoded(content_type: &Mime) -> bool {
	content_type.essence_str() == APPLICATION_WWW_FORM_URLENCODED.as_ref()
}

async fn parse_urlencoded<T: FormDataBuilder>(body: Body) -> Result<T::Data, Error<T::Err>> {
	let body = body::to_bytes(body).await?;

	let mut builder = T::default();
	for (name, value) in form_urlencoded::parse(&body) {
		let bytes = Bytes::copy_from_slice(value.as_bytes());
		builder
			.add_entry(name, stream::once(async move { Ok(bytes) }).boxed())
			.await?;
	}
	builder.build()
}

fn is_multipart(content_type: &Mime) -> bool {
	content_type.essence_str() == MULTIPART_FORM_DATA.as_ref()
}

async fn parse_multipart<T: FormDataBuilder>(body: Body, content_type: &Mime) -> Result<T::Data, Error<T::Err>> {
	let boundary = content_type.get_param(BOUNDARY).ok_or(Error::MissingBoundary)?.as_str();
	let mut multipart = Multipart::new(body, boundary);

	let mut builder = T::default();
	while let Some(field) = multipart.next_field().await? {
		let name = field.name().ok_or(Error::MissingContentDisposition)?;
		// unfortunately, we need to clone the name so we can move field which is our only way
		// of accessing the stream
		let name = name.to_owned();

		let value = field.map_err(Into::into);
		builder.add_entry(name.into(), value.boxed()).await?;
	}
	builder.build()
}

/*!
This crate is an extension to the popular [gotham web framework][gotham] for Rust. It aims to
reduce boilerplate necessary to read request bodies today as a stop-gap until gotham finally
implements a [body extractor].

# :sparkles: Features

- Parse `application/x-www-form-urlencoded` request bodies
- Parse `multipart/form-data` request bodies
- Verify the parsed request body

# :warning: Warning

This crate is asynchronous, but does not yet enforce uploads limits. **YOU ARE RESPONSIBLE
FOR ENFORCING UPLOAD LIMITS.**

# :spiral_notepad: Example

```rust
# use gotham::handler::HandlerError;
# use gotham::helpers::http::response::*;
# use gotham::hyper::{Body, Response, StatusCode};
# use gotham::state::State;
# use mime::TEXT_PLAIN;
use gotham_formdata::FormData;

#[derive(FormData)]
struct LoginData {
	#[validate(regex = "[a-zA-Z0-9_]", error = "The username contains illegal characters.")]
	username: String,
	#[validate(min_length = 6)]
	password: String
}

async fn login_handler(state: &mut State) -> Result<Response<Body>, HandlerError> {
	let login_data: LoginData = FormData::parse_form_data(state).await?;
	Ok(if login_data.password == "secret" {
		create_response(state, StatusCode::OK, TEXT_PLAIN, login_data.username)
	} else {
		create_empty_response(state, StatusCode::FORBIDDEN)
	})
}
