use super::Validator;
use regex_crate::Regex;
use std::{cell::Cell, mem::MaybeUninit, sync::Once};
use thiserror::Error;

/// Lazy Regex creation, similar to `lazy_static`. NOT PUBLIC API.
#[doc(hidden)]
#[allow(missing_debug_implementations)]
pub struct LazyRegex {
	raw: &'static str,
	re: Cell<MaybeUninit<Result<Regex, regex_crate::Error>>>,
	once: Once
}

impl LazyRegex {
	#[doc(hidden)]
	pub const fn new(raw: &'static str) -> Self {
		Self {
			raw,
			re: Cell::new(MaybeUninit::uninit()),
			once: Once::new()
		}
	}

	#[doc(hidden)]
	pub fn get(&'static self) -> Result<&'static Regex, regex_crate::Error> {
		self.once.call_once(|| self.re.set(MaybeUninit::new(Regex::new(self.raw))));

		// self.re is guaranteed to be initialized at this point
		let re = unsafe { &*(*self.re.as_ptr()).as_ptr() };

		match re {
			Ok(re) => Ok(&re),
			Err(err) => Err(err.clone())
		}
	}
}

// regex::Regex and regex::Error are both Sync, so this should be fine
unsafe impl Sync for LazyRegex {}

/// This error is emitted by the [RegexValidator] if the value did not match the regex.
#[derive(Clone, Copy, Debug, Error)]
#[error("Value is smaller than minimum of {0}")]
pub struct RegexMismatchError<'re>(&'re Regex);

/// A validator that checks that an integer is at least of a minimal value.
#[derive(Clone, Debug)]
pub struct RegexValidator<'re> {
	re: &'re Regex
}

impl<'re> RegexValidator<'re> {
	/// Create a new [RegexValidator].
	pub fn new(re: &'re Regex) -> Self {
		Self { re }
	}
}

impl<'re, T: AsRef<str>> Validator<T> for RegexValidator<'re> {
	type Err = RegexMismatchError<'re>;

	fn validate(self, data: &T) -> Result<(), Self::Err> {
		if !self.re.is_match(data.as_ref()) {
			return Err(RegexMismatchError(&self.re));
		}
		Ok(())
	}
}

use either::Either;
use std::{convert::Infallible, marker::PhantomData};

/**
This trait allows data of type `T` to be verified against custom criteria.

Example:

```rust
use gotham_formdata::FormData;

fn validate_password(password: &String) -> Result<(), &'static str> {
	if password.len() < 8 {
		return Err("Password is too short");
		}
	Ok(())
}

#[derive(FormData)]
struct LoginData {
	username: String,
	#[validate(validator = validate_password)]
	password: String
}
```
*/
pub trait Validator<T: ?Sized> {
	/// The error returned when validation failed.
	type Err;

	/// Performs the validation.
	fn validate(self, data: &T) -> Result<(), Self::Err>;
}

/// Convert `()` into an allways accepting validator.
impl<T: ?Sized> Validator<T> for () {
	type Err = Infallible;

	fn validate(self, _: &T) -> Result<(), Infallible> {
		Ok(())
	}
}

/// Convert any function with the correct signature into a validator.
impl<F, Err, T: ?Sized> Validator<T> for F
where
	F: Fn(&T) -> Result<(), Err>
{
	type Err = Err;

	fn validate(self, data: &T) -> Result<(), Err> {
		self(data)
	}
}

/// This struct combines two validators and only validates its input if both validators pass it.
#[derive(Debug, Clone)]
pub struct CombinedValidator<T: ?Sized, V: Validator<T>, W: Validator<T>>(V, W, PhantomData<T>);

impl<T: ?Sized, V: Validator<T>, W: Validator<T>> CombinedValidator<T, V, W> {
	/// Construct a new [CombinedValidator] from two other validators.
	pub fn new(first_validator: V, second_validator: W) -> Self {
		Self(first_validator, second_validator, Default::default())
	}
}

impl<T: ?Sized, V: Validator<T>, W: Validator<T>> Validator<T> for CombinedValidator<T, V, W> {
	type Err = Either<V::Err, W::Err>;

	fn validate(self, data: &T) -> Result<(), Self::Err> {
		self.0.validate(data).map_err(Either::Left)?;
		self.1.validate(data).map_err(Either::Right)
	}
}

