use super::Field;
use proc_macro2::{Ident, TokenStream};
use syn::Generics;

pub(super) struct FormDataBuilder<'a> {
	pub(super) name: &'a Ident,
	pub(super) ident: Ident,
	pub(super) generics: &'a Generics,
	pub(super) fields: &'a [Field]
}

impl<'a> FormDataBuilder<'a> {
	pub(super) fn gen_struct(&self) -> TokenStream {
		let ident = &self.ident;
		let (impl_gen, _, were) = self.generics.split_for_impl();

		let field_names = self.fields.iter().map(|f| &f.ident);
		let field_types = self.fields.iter().map(|f| &f.ty);

		quote! {
			#[doc(hidden)]
			struct #ident #impl_gen #were {
				#( #field_names: Option<#field_types> ),*
			}
		}
	}

	pub(super) fn gen_default_impl(&self) -> TokenStream {
		let ident = &self.ident;
		let (impl_gen, ty_gen, were) = self.generics.split_for_impl();

		let field_names = self.fields.iter().map(|f| &f.ident);

		quote! {
			impl #impl_gen Default for #ident #ty_gen #were {
				fn default() -> Self {
					Self {
						#( #field_names: None ),*
					}
				}
			}
		}
	}

	pub(super) fn gen_builder_impl(&self) -> TokenStream {
		let name = &self.name;
		let ident = &self.ident;
		let (impl_gen, ty_gen, were) = self.generics.split_for_impl();

		let field_names = self.fields.iter().map(|f| &f.ident).collect::<Vec<_>>();
		let field_types = self.fields.iter().map(|f| &f.ty);

		quote! {
			impl #impl_gen ::gotham_formdata::private::FormDataBuilder for #ident #ty_gen #were {
				type Data = #name #ty_gen;

				fn add_entry<'a>(
						&'a mut self,
						name: ::std::borrow::Cow<'a, str>,
						value: ::gotham_formdata::value::Value<'a, ::gotham_formdata::Error>
				) -> ::gotham_formdata::private::FormDataBuilderFuture<'a> {
					#[allow(unused_imports)]
					use ::gotham_formdata::{conversion::prelude::*, private::{FutureExt, StreamExt}};

					async move {
						let name: &str = &name;
						match name {
							#(stringify!(#field_names) => {
								log::debug!("Found value for field {}", name);
								let value_parsed = <#field_types>::convert_value(name, value).await?;
								self.#field_names.replace(value_parsed);
								Ok(())
							},)*
							_ => {
								log::debug!("Found an unknown field: {}", name);
								Err(::gotham_formdata::Error::UnknownField(name.to_string()))
							}
						}
					}.boxed()
				}

				fn build(self) -> Result<#name #ty_gen, ::gotham_formdata::Error> {
					Ok(#name #ty_gen {
						#( #field_names: self.#field_names.ok_or(::gotham_formdata::Error::MissingField(stringify!(#field_names).to_owned()))? ),*
					})
				}
			}
		}
	}
}

use proc_macro2::{Ident, Span};
use syn::{
	parse::{Parse, ParseStream},
	spanned::Spanned,
	Error, Expr, Result, Token, Type
};

#[allow(dead_code)]
struct FieldMeta {
	ident: Ident,
	eq_token: Token![=],
	expr: Expr
}

impl Parse for FieldMeta {
	fn parse(input: ParseStream<'_>) -> Result<Self> {
		Ok(Self {
			ident: input.parse()?,
			eq_token: input.parse()?,
			expr: input.parse()?
		})
	}
}

pub(super) struct Field {
	pub(super) ident: Ident,
	pub(super) ty: Type
}

impl Field {
	pub(super) fn new(field: syn::Field) -> Result<Self> {
		let span = field.span();

		let mut ident = field
			.ident
			.ok_or(Error::new(span, "Fields without an ident are not supported"))?;
		ident.set_span(Span::call_site());

		Ok(Self { ident, ty: field.ty })
	}
}

use crate::util::*;
use proc_macro2::{Span, TokenStream};
use syn::{Data, DeriveInput, Error, Fields, Result};

mod builder;
use builder::FormDataBuilder;

mod field;
use field::Field;

pub(super) fn expand(input: DeriveInput) -> Result<TokenStream> {
	let name = &input.ident;
	let (impl_gen, ty_gen, were) = input.generics.split_for_impl();
	let strukt = match input.data {
		Data::Struct(strukt) => strukt,
		_ => {
			return Err(Error::new(
				Span::call_site(),
				"#[derive(FormData)] can only be used on structs"
			))
		},
	};

	let fields = match strukt.fields {
		Fields::Named(named) => named.named.into_iter().map(|field| Field::new(field)).collect_to_result()?,
		Fields::Unnamed(_) => {
			return Err(Error::new(
				Span::call_site(),
				"#[derive(FormData)] cannot be used on tuple structs"
			))
		},
		Fields::Unit => Vec::new()
	};

	let builder = FormDataBuilder {
		name,
		ident: format_ident!("{}FormDataBuilder", name),
		generics: &input.generics,
		fields: &fields
	};
	let builder_ident = &builder.ident;

	let builder_struct = builder.gen_struct();
	let builder_default_impl = builder.gen_default_impl();
	let builder_builder_impl = builder.gen_builder_impl();

	let mut dummy = format_ident!("_IMPL_FORMDATA_FOR_{}", name);
	dummy.set_span(Span::call_site());
	Ok(quote! {
		#[allow(non_upper_case_globals)]
		static #dummy: () = {
			#builder_struct
			#builder_default_impl
			#builder_builder_impl

			impl #impl_gen ::gotham_formdata::FormData for #name #ty_gen #were {
				type Err = ::gotham_formdata::Error;

				fn parse_form_data(state: &mut ::gotham_formdata::private::State) -> ::gotham_formdata::FormDataFuture<Self> {
					use ::gotham_formdata::private::FutureExt as _;

					let content_type = ::gotham_formdata::private::get_content_type(state);
					let body = ::gotham_formdata::private::get_body(state);

					async move {
						let content_type = content_type?;
						::log::debug!("Parsing Form Data for type {} with Content-Type {}", stringify!(#name), content_type);

						let res = ::gotham_formdata::private::parse::<#builder_ident #ty_gen>(body, content_type).await?;
						::gotham_formdata::private::Validate::validate(&res)?;
						Ok(res)
					}.boxed()
				}
			}
		};
	})
}

//! This crate implements derive macros for the `gotham_formdata` crate.
#![warn(missing_docs, rust_2018_idioms)]
#![deny(unreachable_pub)]
#![forbid(unsafe_code)]

#[macro_use]
extern crate quote;

use proc_macro::TokenStream;
use proc_macro2::TokenStream as TokenStream2;
use syn::{parse_macro_input, DeriveInput, Result};

mod form_data;
mod util;

#[inline]
fn print_tokens(tokens: TokenStream2) -> TokenStream {
	//eprintln!("{}", tokens);
	tokens.into()
}

#[inline]
fn expand_derive<F>(input: TokenStream, expand: F) -> TokenStream
where
	F: FnOnce(DeriveInput) -> Result<TokenStream2>
{
	print_tokens(expand(parse_macro_input!(input)).unwrap_or_else(|err| err.to_compile_error()))
}

/// This derive macro implements `FormData` for the struct it is invoked on. Enums, unions and
/// tuple structs are not supported.
#[proc_macro_derive(FormData, attributes(validate))]
pub fn derive_form_data(input: TokenStream) -> TokenStream {
	expand_derive(input, form_data::expand)
}

use proc_macro2::Span;
use syn::{Error, Path};

pub(crate) trait CollectToResult {
	type Item;

	fn collect_to_result(self) -> Result<Vec<Self::Item>, Error>;
}

impl<Item, I> CollectToResult for I
where
	I: Iterator<Item = Result<Item, Error>>
{
	type Item = Item;

	fn collect_to_result(self) -> Result<Vec<Item>, Error> {
		self.fold(<Result<Vec<Item>, Error>>::Ok(Vec::new()), |res, code| match (code, res) {
			(Ok(code), Ok(mut codes)) => {
				codes.push(code);
				Ok(codes)
			},
			(Ok(_), Err(errors)) => Err(errors),
			(Err(err), Ok(_)) => Err(err),
			(Err(err), Err(mut errors)) => {
				errors.combine(err);
				Err(errors)
			}
		})
	}
}

pub(crate) trait PathEndsWith {
	fn ends_with(&self, s: &str) -> bool;
}

impl PathEndsWith for Path {
	fn ends_with(&self, s: &str) -> bool {
		self.segments.last().map(|segment| segment.ident.to_string()).as_deref() == Some(s)
	}
}

pub(crate) trait WithSpan {
	fn with_span(self, span: Span) -> Self;
}

impl WithSpan for Error {
	fn with_span(self, span: Span) -> Self {
		let mut err: Option<Self> = None;
		for old_err in self {
			let new_err = Error::new(span, old_err);
			err = match err {
				Some(mut err) => {
					err.combine(new_err);
					Some(err)
				},
				None => Some(new_err)
			};
		}
		err.unwrap()
	}
}

/*!
This mod contains conversion traits for common used types, that allows them to be created from
a stream of bytes. Furthermore, it allows every type that implements [FromStr] plus some other
common types to be converted.

You cannot implement any of these traits manually. This is intentional. Instead, if you want to
provide a custom conversion method, just implement it as a method for your type:

```rust
use futures_util::{FutureExt, StreamExt};
use gotham_formdata::{conversion::ConversionFuture, value::{BytesOrString, Value}, FormData};
use validator::Validate;

/// This type parses Base64-encoded values to a [Vec<u8>].
struct Base64(Vec<u8>);

impl Base64 {
	// the method signature needs to be roughly equivalent to this
	async fn convert_value(
			name: &str,
			value: Value<'_, gotham_formdata::Error>
	) -> Result<Self, gotham_formdata::Error> {
		let decoded = match value.value {
			BytesOrString::Bytes(mut stream) => {
				let mut buf: Vec<u8> = Vec::new();
				while let Some(data) = stream.next().await {
					buf.extend_from_slice(&data?);
				}
				base64::decode(&buf)
			},
			BytesOrString::String(string) => base64::decode(string.as_bytes())
		}.map_err(|err| gotham_formdata::Error::IllegalField(name.to_owned(), err.into()))?;

		Ok(Self(decoded))
	}
}

#[derive(FormData, Validate)]
struct MyData {
	foo: Base64
}
# mod base64 { pub fn decode(input: &[u8]) -> Result<Vec<u8>, std::convert::Infallible> { unimplemented!() } }
```
*/

use crate::{
	value::{BytesOrString, Value},
	Error
};
use bytes::{Bytes, BytesMut};
use futures_util::{future::FutureExt, stream::StreamExt};
use gotham::anyhow;
use std::{future::Future, pin::Pin, str::FromStr};

/// Re-exports for use in derive macro.
#[doc(hidden)]
pub mod prelude {
	pub use super::{ConvertFromStr, ConvertRawBytes};
}

mod private {
	/// This trait ensures that none of the conversion traits can be implemented manually.
	pub trait Sealed1 {}

	/// This trait ensures that none of the conversion traits can be implemented manually.
	pub trait Sealed2 {}
}

/// The future returned from conversion methods.
pub type ConversionFuture<'a, T, Err> = Pin<Box<dyn Future<Output = Result<T, Err>> + Send + 'a>>;

/// This trait is used to convert types that implement [FromStr] from a stream of bytes.
///
/// It is not possible to implement this trait manually. This is intentional. Look at the
/// [module documentation](self) for an example how to convert custom types.
pub trait ConvertFromStr<Err>: private::Sealed1 + Sized {
	/// Perform the conversion.
	fn convert_value<'a>(name: &'a str, value: Value<'a, Err>) -> ConversionFuture<'a, Self, Err>;
}

impl<T> private::Sealed1 for T
where
	T: FromStr,
	T::Err: Into<anyhow::Error>
{
}

impl<T> ConvertFromStr<Error> for T
where
	T: FromStr,
	T::Err: Into<anyhow::Error>
{
	fn convert_value<'a>(name: &'a str, value: Value<'a, Error>) -> ConversionFuture<'a, Self, Error> {
		async move {
			let buf = match value.value {
				BytesOrString::Bytes(mut stream) => {
					let mut buf = String::new();
					while let Some(data) = stream.next().await {
						let data = data?;
						let str = String::from_utf8_lossy(data.as_ref());
						buf.push_str(&str);
					}
					buf.into()
				},
				BytesOrString::String(buf) => buf
			};

			buf.parse::<Self>()
				.map_err(|err| Error::IllegalField(name.to_owned(), err.into()))
		}
		.boxed()
	}
}

/// This trait is used to convert `Vec<u8>` and similar types from a stream of bytes.
///
/// It is not possible to implement this trait manually. This is intentional. Look at the
/// [module documentation](self) for an example how to convert custom types.
pub trait ConvertRawBytes<'a, Err>: private::Sealed2 + Sized {
	/// Perform the conversion.
	fn convert_value(name: &'a str, value: Value<'a, Err>) -> ConversionFuture<'a, Self, Err>;
}

impl private::Sealed2 for Vec<u8> {}

impl<'a, Err: 'a> ConvertRawBytes<'a, Err> for Vec<u8> {
	fn convert_value(_name: &'a str, value: Value<'a, Err>) -> ConversionFuture<'a, Self, Err> {
		async move {
			match value.value {
				BytesOrString::Bytes(mut stream) => {
					let mut buf: Vec<u8> = Vec::new();
					while let Some(data) = stream.next().await {
						buf.extend_from_slice(&data?);
					}
					Ok(buf)
				},
				BytesOrString::String(string) => Ok(string.as_bytes().to_vec())
			}
		}
		.boxed()
	}
}

impl private::Sealed2 for BytesMut {}

impl<'a, Err: 'a> ConvertRawBytes<'a, Err> for BytesMut {
	fn convert_value(_name: &'a str, value: Value<'a, Err>) -> ConversionFuture<'a, Self, Err> {
		async move {
			match value.value {
				BytesOrString::Bytes(mut stream) => {
					let mut buf = BytesMut::new();
					while let Some(data) = stream.next().await {
						buf.extend_from_slice(&data?);
					}
					Ok(buf)
				},
				BytesOrString::String(string) => Ok(string.as_bytes().into())
			}
		}
		.boxed()
	}
}

impl private::Sealed2 for Bytes {}

impl<'a, Err: 'a> ConvertRawBytes<'a, Err> for Bytes {
	fn convert_value(name: &'a str, value: Value<'a, Err>) -> ConversionFuture<'a, Self, Err> {
		BytesMut::convert_value(name, value).map(|res| res.map(Into::into)).boxed()
	}
}

use gotham::anyhow;
use mime::Mime;
use thiserror::Error;
use validator::ValidationErrors;

#[derive(Debug, Error)]
#[non_exhaustive]
/// This error type is used when parsing form data from a request body was unsuccessful.
pub enum Error {
	/// The body of the request could not be read.
	#[error("The body of the request could not be read")]
	IllegalBody(#[from] gotham::hyper::Error),
	/// The content type of the body was not a valid mime type.
	#[error("The 'Content-Type' header could not be parsed: {0}")]
	IllegalContentType(#[from] mime::FromStrError),
	/// The content type of the body contained unreadable bytes.
	#[error("The 'Content-Type' header could not be parsed: {0}")]
	IllegalContentTypeValue(#[from] gotham::hyper::header::ToStrError),
	/// The value of a field could not be parsed into that field's type.
	#[error("The field {0} could not be parsed: {1}")]
	IllegalField(String, #[source] anyhow::Error),
	/// The body was parsed but contained data that did not pass validation.
	#[error("The body contained invalid data: {0}")]
	InvalidData(#[from] ValidationErrors),
	/// An I/O error occured while reading the body.
	#[error("I/O Error while reading body: {0}")]
	IoError(#[from] std::io::Error),
	/// The body was a multipart body but did not specify a boundary.
	#[error("The request failed to specify the multipart boundary")]
	MissingBoundary,
	/// The request did not specify a content type.
	#[error("The request is missing a 'Content-Type' header")]
	MissingContentType,
	/// One of the multipart parts is missing a `Content-Disposition` header.
	#[error("Missing a 'Content-Disposition' header")]
	MissingContentDisposition,
	/// The body is missing a required field.
	#[error("Missing Field '{0}'")]
	MissingField(String),
	/// The multipart format was invalid.
	#[error("Multipart error: {0}")]
	MultipartError(#[from] multer::Error),
	/// The body's content type is not supported.
	#[error("Unknown 'Content-Type' header value: {0}")]
	UnknownContentType(Mime),
	/// The body contained a field that was not expected.
	#[error("Unknown Field '{0}'")]
	UnknownField(String)
}

use gotham::state::State;
use std::{future::Future, pin::Pin};

/// This is the return type of [FormData::parse_form_data].
#[allow(type_alias_bounds)]
pub type FormDataFuture<T: FormData> = Pin<Box<dyn Future<Output = Result<T, T::Err>> + Send>>;

/**
This is the trait implemented by `#[derive(FormData)]`. It provides a method to parse the struct
it is implemented for to be parsed from the request body contained in gotham's state.

You usually don't implement this trait directly, use the derive macro instead.
*/
pub trait FormData: Sized {
	/// The error type returned when parsing the request body was unsuccessful.
	type Err;

	/// Parse the struct from the request body contained in gotham's state.
	fn parse_form_data(state: &mut State) -> FormDataFuture<Self>;
}

/**
This is the equivalent of [FormData] from the state's perspective. Use this if you prefer
`state.parse_form_data::<MyData>()?` over `MyData::parse_form_data(&mut state)?`.
*/
pub trait FormDataFromState {
	/// Parse `T` from the request body contained in this state.
	fn parse_form_data<T: FormData>(&mut self) -> FormDataFuture<T>;
}

impl FormDataFromState for State {
	fn parse_form_data<T: FormData>(&mut self) -> FormDataFuture<T> {
		T::parse_form_data(self)
	}
}

/*!
This crate is an extension to the popular [gotham web framework][gotham] for Rust. It aims to
reduce boilerplate necessary to read request bodies today as a stop-gap until gotham finally
implements a [body extractor].

# :sparkles: Features

- Parse `application/x-www-form-urlencoded` request bodies
- Parse `multipart/form-data` request bodies
- Verify the parsed request body
- `#![forbid(unsafe_code)]` ensures that all functionality is implemented in 100% safe Rust code

# :warning: Warning

This crate is asynchronous, but does not yet enforce uploads limits. **YOU ARE RESPONSIBLE
FOR ENFORCING UPLOAD LIMITS.**

# :spiral_notepad: Example

```rust
# use gotham::handler::HandlerError;
# use gotham::helpers::http::response::*;
# use gotham::hyper::{Body, Response, StatusCode};
# use gotham::state::State;
# use mime::TEXT_PLAIN;
use gotham_formdata::FormData;
use validator::Validate;

#[derive(FormData, Validate)]
struct LoginData {
	#[validate(length(min = 5, max = 16))]
	username: String,
	#[validate(length(min = 8))]
	password: String
}

async fn login_handler(state: &mut State) -> Result<Response<Body>, HandlerError> {
	let login_data: LoginData = FormData::parse_form_data(state).await?;
	Ok(if login_data.password == "secret" {
		create_response(state, StatusCode::OK, TEXT_PLAIN, login_data.username)
	} else {
		create_empty_response(state, StatusCode::FORBIDDEN)
	})
}
```

